const sharp = require('sharp');
const fs = require('fs');
const path = require('path');

const STICKERS_DIR = path.join(__dirname, 'public', 'assets', 'stickers');

if (!fs.existsSync(STICKERS_DIR)) {
  fs.mkdirSync(STICKERS_DIR, { recursive: true });
}

async function createSticker(name, text, bgColor, textColor) {
  const svg = `
    <svg width="400" height="150" viewBox="0 0 400 150" xmlns="http://www.w3.org/2000/svg">
      <rect x="10" y="10" width="380" height="130" rx="30" fill="${bgColor}" stroke="white" stroke-width="5"/>
      <text x="200" y="85" font-family="Arial, sans-serif" font-size="40" font-weight="bold" fill="${textColor}" text-anchor="middle" dominant-baseline="middle">${text}</text>
    </svg>
  `;

  await sharp(Buffer.from(svg))
    .png()
    .toFile(path.join(STICKERS_DIR, `${name}.png`));
  console.log(`Generated ${name}.png`);
}

async function run() {
  await createSticker('free-delivery', 'FREE DELIVERY', '#FF5722', '#FFFFFF');
  await createSticker('low-shipping', 'LOW SHIPPING', '#4CAF50', '#FFFFFF');
  await createSticker('shipping-badge', '₹ SHIPPING', '#2196F3', '#FFFFFF');
}

run().catch(console.error);

import { NextRequest, NextResponse } from "next/server";
import { v4 as uuidv4 } from "uuid";
import { generate50Variants } from "@/lib/image/generateVariants";

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();
    const image = formData.get("image") as File;

    if (!image) {
      return NextResponse.json({ error: "No image provided" }, { status: 400 });
    }

    const buffer = Buffer.from(await image.arrayBuffer());
    const baseName = uuidv4();
    const variants = await generate50Variants(buffer, baseName);

    return NextResponse.json({
      message: "50 variants generated successfully",
      variants,
    });
  } catch (error: any) {
    console.error("Generation error:", error);
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import prisma from "@/lib/db/prisma";
import { generateHash } from "@/lib/hash/hashImage";

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();
    const image = formData.get("image") as File;

    if (!image) {
      return NextResponse.json({ error: "No image provided" }, { status: 400 });
    }

    const buffer = Buffer.from(await image.arrayBuffer());
    const hash = await generateHash(buffer);

    const product = await prisma.product.findUnique({
      where: { hash },
    });

    if (!product) {
      return NextResponse.json({
        message: "No shipping charge found for this image",
        hash,
        shippingCharge: null,
      }, { status: 404 });
    }

    return NextResponse.json({
      hash,
      shippingCharge: product.shippingCharge,
    });
  } catch (error: any) {
    console.error("Get shipping error:", error);
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import path from 'path';
import fs from 'fs';

export async function GET(
  req: NextRequest,
  { params }: { params: Promise<{ sessionId: string; id: string }> }
) {
  try {
    const { sessionId, id } = await params;
    const imagePath = path.join(process.cwd(), 'tmp', sessionId, 'output', `${id}.png`);

    if (!fs.existsSync(imagePath)) {
      return NextResponse.json({ error: 'Image not found' }, { status: 404 });
    }

    const imageBuffer = fs.readFileSync(imagePath);

    return new Response(imageBuffer, {
      headers: {
        'Content-Type': 'image/png',
        'Cache-Control': 'public, max-age=3600, must-revalidate',
      },
    });
  } catch (error: any) {
    console.error('Image serving error:', error);
    return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { syncMeeshoCharge } from "@/lib/meesho/meeshoSync";

export async function POST(request: NextRequest) {
  try {
    const { hash, charge } = await request.json();

    if (!hash || charge === undefined) {
      return NextResponse.json({ error: "Missing hash or charge" }, { status: 400 });
    }

    const product = await syncMeeshoCharge(hash, charge);

    return NextResponse.json({
      message: "Synchronized with Meesho successfully",
      product,
    });
  } catch (error: any) {
    console.error("Meesho sync error:", error);
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { calculateMeeshoShipping } from '@/lib/shipping/calc';

export async function POST(req: NextRequest) {
  try {
    const body = await req.json();
    const { weight, zone } = body;

    if (weight === undefined || !zone) {
      return NextResponse.json({ error: 'Missing weight or zone' }, { status: 400 });
    }

    const result = calculateMeeshoShipping(Number(weight), zone);

    return NextResponse.json(result);
  } catch (error: any) {
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { v4 as uuidv4 } from "uuid";
import prisma from "@/lib/db/prisma";
import { generateHash } from "@/lib/hash/hashImage";
import { generate50Variants } from "@/lib/image/generateVariants";
import { calculateMeeshoShipping } from "@/lib/shipping/calc";

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();
    const image = formData.get("image") as File;

    if (!image) {
      return NextResponse.json({ error: "No image provided" }, { status: 400 });
    }

    const buffer = Buffer.from(await image.arrayBuffer());
    const hash = await generateHash(buffer);

    // Check if exists
    let product = await prisma.product.findUnique({
      where: { hash },
    });

    if (product) {
      return NextResponse.json({
        message: "Image already exists",
        product,
      });
    }

    // Generate variants
    const baseName = uuidv4();
    const variants = await generate50Variants(buffer, baseName);

    // Initial shipping estimate (approx)
    const shippingCharge = 45; // Default for new upload

    // Save to DB
    product = await prisma.product.create({
      data: {
        hash,
        imagePath: variants[0], // Reference to first variant
        shippingCharge,
      },
    });

    return NextResponse.json({
      message: "Uploaded and processed successfully",
      hash,
      shippingCharge,
      variants,
    });
  } catch (error: any) {
    console.error("Upload error:", error);
    return NextResponse.json({ error: error.message }, { status: 500 });
  }
}

'use client';

import { useState } from 'react';
import { CATEGORIES } from '@/lib/constants';
import { Upload, Package, Download, RefreshCw, CheckCircle2, AlertCircle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export default function Home() {
  const [selectedCategory, setSelectedCategory] = useState(CATEGORIES[0].id);
  const [image, setImage] = useState<File | null>(null);
  const [preview, setPreview] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [generatedImages, setGeneratedImages] = useState<{ id: number; shipping: number }[]>([]);
  const [error, setError] = useState<string | null>(null);

  // Shipping Calculator State
  const [calcWeight, setCalcWeight] = useState('');
  const [calcZone, setCalcZone] = useState<'LOCAL' | 'REGIONAL' | 'NATIONAL'>('LOCAL');
  const [calcResult, setCalcResult] = useState<any>(null);
  const [isCalculating, setIsCalculating] = useState(false);
  const [calcError, setCalcError] = useState<string | null>(null);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
      setError(null);
      setIsComplete(false);
      setGeneratedImages([]);
      setSessionId(null);
    }
  };

  const handleGenerate = async () => {
    if (!image) {
      setError('Please upload a product image first.');
      return;
    }

    setIsGenerating(true);
    setError(null);
    setIsComplete(false);
    setGeneratedImages([]);
    setSessionId(null);

    const formData = new FormData();
    formData.append('image', image);
    formData.append('categoryId', selectedCategory);

    try {
      const response = await fetch('/api/generate', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Generation failed');
      }

      const data = await response.json();
      setSessionId(data.sessionId);
      setGeneratedImages(data.images);
      setIsComplete(true);
    } catch (err: any) {
      setError(err.message || 'An unexpected error occurred.');
    } finally {
      setIsGenerating(false);
    }
  };

  const handleDownloadSingleImage = async (imgId: number) => {
    if (!sessionId) return;

    try {
      const url = `/api/image/${sessionId}/${imgId}`;
      const response = await fetch(url);
      if (!response.ok) throw new Error('Download failed');

      const blob = await response.blob();
      const downloadUrl = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = downloadUrl;
      a.download = `variation-${imgId}.png`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(downloadUrl);
    } catch (err) {
      console.error('Download error:', err);
    }
  };

  const handleCalculateShipping = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!calcWeight || isNaN(Number(calcWeight))) {
      setCalcError('Please enter a valid weight in kg (e.g., 0.5).');
      return;
    }

    setIsCalculating(true);
    setCalcError(null);
    setCalcResult(null);

    try {
      const response = await fetch('/api/shipping/calculate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ weight: Number(calcWeight), zone: calcZone }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Calculation failed');

      setCalcResult(data);
    } catch (err: any) {
      setCalcError(err.message || 'An unexpected error occurred.');
    } finally {
      setIsCalculating(false);
    }
  };

  return (
    <main className="min-h-screen bg-slate-50 text-slate-900 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="text-center mb-12">
          <motion.h1
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-4xl font-extrabold text-slate-900 tracking-tight sm:text-5xl"
          >
            Marketplace Generator
          </motion.h1>
          <motion.p
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            className="mt-4 text-lg text-slate-600"
          >
            Generate 57 marketplace-ready variations from one image.
          </motion.p>
        </div>

        <div className="bg-white rounded-2xl shadow-xl overflow-hidden border border-slate-200">
          <div className="p-8">
            <div className="space-y-6">
              {/* Category Selection */}
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Select Product Category
                </label>
                <div className="relative">
                  <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="block w-full pl-4 pr-10 py-3 text-base border-slate-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-lg border appearance-none transition-all cursor-pointer hover:border-slate-400"
                  >
                    {CATEGORIES.map((cat) => (
                      <option key={cat.id} value={cat.id}>
                        {cat.name}
                      </option>
                    ))}
                  </select>
                  <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-slate-500">
                    <Package className="h-5 w-5" />
                  </div>
                </div>
              </div>

              {/* Image Upload */}
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Upload Product Image
                </label>
                <div
                  className={`mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-dashed rounded-lg transition-all ${preview ? 'border-indigo-300 bg-indigo-50' : 'border-slate-300 hover:border-slate-400'
                    }`}
                >
                  <div className="space-y-2 text-center">
                    {preview ? (
                      <div className="relative inline-block">
                        <img
                          src={preview}
                          alt="Preview"
                          className="mx-auto h-48 w-48 object-contain rounded-md"
                        />
                        <button
                          onClick={() => { setPreview(null); setImage(null); }}
                          className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 shadow-lg hover:bg-red-600"
                        >
                          <RefreshCw className="h-4 w-4" />
                        </button>
                      </div>
                    ) : (
                      <>
                        <Upload className="mx-auto h-12 w-12 text-slate-400" />
                        <div className="flex text-sm text-slate-600">
                          <label className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none">
                            <span>Upload a file</span>
                            <input
                              type="file"
                              className="sr-only"
                              accept="image/*"
                              onChange={handleImageChange}
                            />
                          </label>
                          <p className="pl-1">or drag and drop</p>
                        </div>
                        <p className="text-xs text-slate-500">PNG, JPG, WebP up to 10MB</p>
                      </>
                    )}
                  </div>
                </div>
              </div>

              {/* Error Message */}
              <AnimatePresence>
                {error && (
                  <motion.div
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: 'auto' }}
                    exit={{ opacity: 0, height: 0 }}
                    className="flex items-center p-4 text-red-800 rounded-lg bg-red-50 border border-red-100"
                  >
                    <AlertCircle className="flex-shrink-0 w-5 h-5 mr-3" />
                    <span className="text-sm font-medium">{error}</span>
                  </motion.div>
                )}
              </AnimatePresence>

              {/* Action Buttons */}
              <div className="pt-4">
                {!isComplete ? (
                  <button
                    onClick={handleGenerate}
                    disabled={isGenerating || !image}
                    className={`w-full flex items-center justify-center py-4 px-6 border border-transparent rounded-lg shadow-sm text-lg font-bold text-white transition-all ${isGenerating || !image
                      ? 'bg-slate-300 cursor-not-allowed'
                      : 'bg-indigo-600 hover:bg-indigo-700 active:transform active:scale-95'
                      }`}
                  >
                    {isGenerating ? (
                      <>
                        <RefreshCw className="animate-spin -ml-1 mr-3 h-6 w-6" />
                        Generating 57 Variations...
                      </>
                    ) : (
                      'Generate Images'
                    )}
                  </button>
                ) : (
                  <div className="space-y-4">
                    <div className="flex items-center justify-center p-4 text-green-800 rounded-lg bg-green-50 border border-green-100 mb-4">
                      <CheckCircle2 className="flex-shrink-0 w-6 h-6 mr-3 text-green-600" />
                      <span className="text-lg font-bold">Successfully generated 57 images!</span>
                    </div>

                    <button
                      onClick={() => { setIsComplete(false); setGeneratedImages([]); }}
                      className="w-full text-sm text-slate-500 hover:text-slate-700 font-medium transition-colors mb-4"
                    >
                      Start Over
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Generated Images Grid */}
        <AnimatePresence>
          {isComplete && generatedImages.length > 0 && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="mt-12"
            >
              <h2 className="text-2xl font-bold text-slate-900 mb-6">Generated Variations</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {generatedImages.map((img) => (
                  <motion.div
                    key={img.id}
                    whileHover={{ y: -5 }}
                    className="bg-white rounded-xl shadow-md overflow-hidden border border-slate-200 group"
                  >
                    <div className="aspect-square relative overflow-hidden bg-slate-100">
                      <img
                        src={`/api/image/${sessionId}/${img.id}`}
                        alt={`Variation ${img.id}`}
                        className="w-full h-full object-contain"
                        loading="lazy"
                      />
                      <div className="absolute top-2 right-2 bg-indigo-600 text-white px-3 py-1 rounded-full text-sm font-bold shadow-lg">
                        Shipping: ₹{img.shipping}
                      </div>
                    </div>
                    <div className="p-4">
                      <button
                        onClick={() => handleDownloadSingleImage(img.id)}
                        className="w-full flex items-center justify-center py-2 px-4 border border-transparent rounded-lg shadow-sm text-sm font-bold text-white bg-indigo-600 hover:bg-indigo-700 transition-all"
                      >
                        <Download className="mr-2 h-4 w-4" />
                        Download Image
                      </button>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Shipping Calculator section */}
        <div className="mt-12 bg-white rounded-2xl shadow-xl overflow-hidden border border-slate-200">
          <div className="p-8">
            <h2 className="text-2xl font-bold text-slate-900 mb-6 flex items-center">
              <Package className="mr-2 h-6 w-6 text-indigo-600" />
              Meesho Shipping Calculator
            </h2>
            <form onSubmit={handleCalculateShipping} className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Product Weight (kg)
                </label>
                <input
                  type="number"
                  step="0.01"
                  value={calcWeight}
                  onChange={(e) => setCalcWeight(e.target.value)}
                  placeholder="e.g. 0.45"
                  className="block w-full px-4 py-3 text-sm border-slate-300 rounded-lg border focus:ring-indigo-500 focus:border-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-2">
                  Destination Zone
                </label>
                <select
                  value={calcZone}
                  onChange={(e) => setCalcZone(e.target.value as any)}
                  className="block w-full px-4 py-3 text-sm border-slate-300 rounded-lg border focus:ring-indigo-500 focus:border-indigo-500 appearance-none cursor-pointer"
                >
                  <option value="LOCAL">Local</option>
                  <option value="REGIONAL">Regional</option>
                  <option value="NATIONAL">National</option>
                </select>
              </div>
              <div className="sm:col-span-2">
                <button
                  type="submit"
                  disabled={isCalculating}
                  className="w-full flex items-center justify-center py-3 px-6 border border-transparent rounded-lg shadow-sm text-sm font-bold text-white bg-indigo-600 hover:bg-indigo-700 transition-all disabled:bg-slate-300"
                >
                  {isCalculating ? <RefreshCw className="animate-spin h-5 w-5" /> : 'Calculate Shipping'}
                </button>
              </div>
            </form>

            {calcError && (
              <div className="mt-6 flex items-center p-4 text-red-800 rounded-lg bg-red-50 border border-red-100">
                <AlertCircle className="flex-shrink-0 w-5 h-5 mr-3" />
                <span className="text-sm font-medium">{calcError}</span>
              </div>
            )}

            {calcResult && (
              <motion.div
                initial={{ opacity: 0, scale: 0.95 }}
                animate={{ opacity: 1, scale: 1 }}
                className="mt-8 p-6 bg-indigo-50 rounded-xl border border-indigo-100"
              >
                <p className="text-sm text-indigo-600 font-bold uppercase tracking-wider mb-2">Calculation Result</p>
                <div className="flex justify-between items-end">
                  <div>
                    <p className="text-3xl font-extrabold text-slate-900">₹{calcResult.charge}</p>
                    <p className="text-sm text-slate-600 mt-1">Weight Slab: <span className="font-bold">{calcResult.slab}</span></p>
                  </div>
                  <div className="text-right">
                    <CheckCircle2 className="h-8 w-8 text-green-500 ml-auto" />
                    <p className="text-xs text-slate-500 mt-1">Zone: {calcResult.zone}</p>
                  </div>
                </div>
              </motion.div>
            )}
          </div>
        </div>

        {/* Info Section */}
        <div className="mt-12 grid grid-cols-1 gap-6 sm:grid-cols-3">
          {[
            { title: 'No AI Used', desc: 'Content is never altered, ensuring marketplace safety.' },
            { title: '57 Variations', desc: 'Unique layouts, borders, and stickers for maximum reach.' },
            { title: 'Marketplace Ready', desc: 'Pre-sized and styled for professional platforms.' }
          ].map((item, idx) => (
            <div key={idx} className="bg-white p-6 rounded-xl border border-slate-200 shadow-sm">
              <h3 className="font-bold text-slate-900 mb-2">{item.title}</h3>
              <p className="text-sm text-slate-600 leading-relaxed">{item.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </main>
  );
}

